'use strict';

// MODULES //

const bench = require( '@stdlib/bench' );
const pkg = require( './../package.json' ).name;
const iterCartesianPower = require( './../lib' );

// MAIN //

bench( pkg, function benchmark( b ) {
	var arr = [ 'a', 'b', 'c' ];
	var n = 2;
	var iterator = iterCartesianPower( arr, n );
	var v;

	// Run the benchmark...
	b.tic();
	for ( var i = 0; i < b.iterations; i++ ) {
		v = iterator.next();
		if ( v.done ) {
			b.fail( 'should iterate over all elements' );
		}
	}
	b.toc();
	if ( v.done ) {
		b.pass( 'benchmark finished' );
	} else {
		b.fail( 'should have finished iteration' );
	}
} );
