'use strict';

// MODULES //
var setReadOnly = require( '@stdlib/utils/define-nonenumerable-read-only-property' );
var isCollection = require( '@stdlib/assert/is-collection' );
var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' ).isPrimitive;
var iteratorSymbol = require( '@stdlib/symbol/iterator' );
var format = require( '@stdlib/string/format' );

// MAIN //

/**
* Returns an iterator which generates the Cartesian power of an input collection.
*
* @param {Collection} x - input collection
* @param {NonNegativeInteger} n - integer power
* @throws {TypeError} first argument must be a collection
* @throws {TypeError} second argument must be a nonnegative integer
* @returns {Iterator} iterator
*
* @example
* var iterCartesianPower = require( '@stdlib/iter/cartesian-power' );
*
* var x = [ 'a', 'b' ];
* var n = 2;
* var iterator = iterCartesianPower( x, n );
*
* var result;
* while ( !( result = iterator.next() ).done ) {
*     console.log( result.value );
* }
* // return
* // [ 'a', 'a' ]
* // [ 'a', 'b' ]
* // [ 'b', 'a' ]
* // [ 'b', 'b' ]
*/
function iterCartesianPower( x, n ) {
	var iter;
	var FLG = false;
	var i = 0;
	var idx = [];

	// Validate input arguments
	if ( !isCollection( x ) ) {
		throw new TypeError( format( 'invalid argument. First argument must be a collection. Value: `%s`.', x ) );
	}
	if ( !isNonNegativeInteger( n ) ) {
		throw new TypeError( format( 'invalid argument. Second argument must be a nonnegative integer. Value: `%s`.', n ) );
	}

	// Initialize index array
	for ( var k = 0; k < n; k++ ) {
		idx.push( 0 );
	}

	// Create an iterator protocol-compliant object
	iter = {};
	setReadOnly( iter, 'next', next );
	setReadOnly( iter, 'return', end );

	// If an environment supports `Symbol.iterator`, make the iterator iterable
	if ( iteratorSymbol ) {
		setReadOnly( iter, iteratorSymbol, factory );
	}
	return iter;

	/**
	 * Returns an iterator protocol-compliant object containing the next iterated value.
	 *
	 * @private
	 * @returns {Object} iterator protocol-compliant object
	 */
	function next() {
		if ( FLG ) {
			return {
				'done': true
			};
		}
		var res = [];
		for ( var k = 0; k < n; k++ ) {
			res.push( x[ idx[ k ] ] );
		}
		// Update indices
		for ( var k = n - 1; k >= 0; k-- ) {
			idx[ k ]++;
			if ( idx[ k ] === x.length ) {
				idx[ k ] = 0;
			} else {
				break;
			}
		}
		i++;
		if ( i >= Math.pow( x.length, n ) ) {
			FLG = true;
		}
		return {
			'value': res,
			'done': false
		};
	}

	/**
	 * Finishes an iterator.
	 *
	 * @private
	 * @param {*} [value] - value to return
	 * @returns {Object} iterator protocol-compliant object
	 */
	function end( value ) {
		FLG = true;
		if ( arguments.length ) {
			return {
				'value': value,
				'done': true
			};
		}
		return {
			'done': true
		};
	}

	/**
	 * Returns a new iterator.
	 *
	 * @private
	 * @returns {Iterator} iterator
	 */
	function factory() {
		return iterCartesianPower( x, n );
	}
}

// EXPORTS //
module.exports = iterCartesianPower;
